vec4 Effect2(vec2 uv) {
    // Create sine wave patterns in both x and y directions
    float sinX = sin(uv.x * 8.0 + iTime * 2.0);
    float sinY = sin(uv.y * 8.0 + iTime * 3.0);

    // Combine the waves with some phase shifting
    vec2 wave = vec2(sinX, sinY) * 0.5 + 0.5;

    // Add turbulence to create dynamic movement
    float turb = turbulent(uv * 4.0 + iTime);

    // Create color transitions using HSL
    vec3 color = hsl(
        mod(iTime * 0.2 + wave.x * 1.5, 1.0),  // Hue
        0.7 + turb * 0.3,                       // Saturation
        0.5 + wave.y * 0.5                      // Lightness
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
