vec4 Effect2(vec2 uv) {
    // Create base noise pattern with turbulence
    vec3 noisePos = vec3(uv * 5.0 + iTime * 0.1, iTime * 0.1);
    float t = turbulent(noisePos);

    // Create color shifts using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.2 + 0.5, 1.0),   // Hue variation over time
        0.7 + t * 0.3,                // Saturation
        0.5 + sin(iTime * 0.4) * 0.5   // Brightness variation
    );

    // Convert to RGB and add glow effect
    vec3 color = hsl2rgb(hslColor);
    float glow = sin(uv.y * pi * 2.0 + iTime) * 0.5 + 0.5;
    color *= glow * 1.5;

    // Add turbulence-based variations
    color += t * 0.3;

    // Soften edges
    float edge = smoothstep(0.8, 1.0, length(uv));
    color *= edge;

    return vec4(color, 1.0);
}
