vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create a geometric pattern using sine waves and turbulence
    float pattern = sin(theta * 8.0) * 0.5 + 
                   sin(theta * 16.0) * 0.3 +
                   turbulent(vec3(st.x, st.y, iTime)) * 0.2;

    // Create dynamic color transitions using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.5 + theta * 0.1, 1.0), // Hue variation over time and space
        0.7, // Saturation
        0.5 + r * 0.4 // Lightness varies with radius
    );

    // Convert HSL to RGB and output
    vec3 color = hsl2rgb(hslColor);

    return vec4(color, 1.0);
}
