vec4 Effect2(vec2 uv) {
    // Animate position over time
    vec2 pos = uv * 5.0 + iTime;

    // Create rotating pattern using sine waves
    float wave1 = sin(pos.x * 0.5 + iTime) * 0.5 + 0.5;
    float wave2 = sin(pos.y * 0.5 + iTime) * 0.5 + 0.5;

    // Combine patterns with rotation and scaling
    vec2 rotatedUV = uv * 4.0 + vec2(sin(iTime)*3.0, cos(iTime)*3.0);

    // Add noise for texture
    float noiseValue = noise(rotatedUV) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 color = hsl(
        sin(iTime * 0.2 + uv.x * 4.0) * 0.5 + 0.5,  // Hue
        0.7,  // Saturation
        noiseValue * (wave1 + wave2) * 0.5 + 0.5);    // Lightness

    return vec4(color, 1.0);
}
