vec4 Effect2(vec2 uv) {
    vec3 c = vec3(0.0);
    float t = float(iTime) * 0.5;

    // Calculate distance from center and add some noise-based movement
    float d = length(uv) + sin(t * 0.5 + length(uv) * 2.0) * 0.3;

    // Create a position vector for turbulence calculation
    vec3 pos = vec3(uv * 8.0, t);
    float m = turbulent(pos) * 0.5 + 0.5;

    // Add color variations using HSL to RGB conversion
    c = hsl2rgb(vec3(
        sin(d * 5.0 + t) * 0.5 + 0.5,   // Hue variation
        1.0 - d * 0.6,                  // Saturation based on distance
        m * 0.8 + 0.2                   // Brightness modulation
    ));

    // Add pulsating glow effect
    c *= sin(t * 3.0 + d * 5.0) * 0.5 + 0.7;

    return vec4(c, 1.0);
}
