vec4 Effect2(vec2 uv) {
    // Center the UV coordinates
    vec2 st = uv * 2.0 - 1.0;

    // Create polar coordinates
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Multiple sine waves with different frequencies and phases
    float wave1 = sin(a * 3.0 + iTime * 2.0) / r;
    float wave2 = sin(a * 5.0 + iTime * 3.0 + PI) / r;
    float wave3 = sin(a * 7.0 + iTime * 4.0 + PI*2.0) / r;

    // Combine waves with RGB offsets
    vec3 color = vec3(wave1, wave2, wave3);

    // Add noise for extra detail
    float noise = turbulent(st * 5.0 + iTime);
    color += noise * 0.5;

    return vec4(clamp(color, 0.0, 1.0), 1.0);
}
