vec4 Effect2(vec2 uv) {
    // Create pulsating radial pattern
    float distance = length(uv);
    float pulse = sin(iTime * 0.5 + distance * 10.0) * 0.5 + 0.5;

    // Add noise and turbulence for organic feel
    vec2 rotatedUV = uv * mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime));
    float noiseValue = turbulent(rotatedUV * 10.0) * 0.5 + 0.5;

    // Create vibrant color transitions using HSL
    vec3 hslColor = vec3(
        distance * 240.0 + iTime * 60.0,   // Hue variation over time and space
        0.8,                                // Saturation
        pulse                               // Brightness modulation
    );

    // Convert to RGB and mix with noise
    vec3 color = hsl2rgb(hslColor) * (noiseValue + turbulent(rotatedUV * 5.0) * 0.5);

    return vec4(color, 1.0);
}
