vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    vec2 st = uv - 0.5;
    float d = length(st);

    // Polar coordinates
    float theta = atan2(st.y, st.x) + iTime * 0.1;

    // Color wheel effect using HSL colors
    vec3 colorWheel = hsl(theta / (2.0 * PI), 0.7, 0.6);

    // Pulsating ring effect
    float pulse = sin(iTime * 0.5 + d * 10.0) * 0.5 + 0.5;
    vec3 colorRing = hsl(0.5, 0.8, pulse);

    // Combine effects with turbulence
    float turb = turbulent(vec2(theta / (2.0 * PI), d));
    vec3 finalColor = mix(colorWheel, colorRing, abs(turb) * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
