vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Add rotation over time
    theta += 0.5 * iTime;

    // Create kaleidoscope effect with 6-fold symmetry
    theta = mod(theta, PI/3.0);
    theta = fract(theta / (PI/3.0)) * (PI/3.0);

    // Create triangle pattern
    float pattern = smoothstep(0.0, 1.0, sin(theta) * r + 0.5);
    pattern += smoothstep(0.0, 1.0, sin(theta + 2.0*PI/3.0) * r + 0.5);
    pattern += smoothstep(0.0, 1.0, sin(theta - 2.0*PI/3.0) * r + 0.5);

    // Add noise and turbulence
    float noiseVal = turbulent(uv * 10.0 + iTime);
    pattern = mix(pattern, noiseVal, 0.2);

    // Create color from pattern
    vec3 color = hsl2rgb(vec3(
        fract(theta/PI + 0.5*iTime),
        0.7,
        smoothstep(0.0, 1.0, r)
    ));

    return vec4(color * pattern, 1.0);
}
