vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    float r = length(uv);
    float theta = atan2(uv.y, uv.x) + iTime;

    // Create rotating sine wave patterns with different frequencies
    float sin1 = sin(theta * 5.0 + iTime * 2.0) * 0.5 + 0.5;
    float sin2 = sin(theta * 10.0 - iTime * 3.0) * 0.5 + 0.5;
    float sin3 = sin(theta * 15.0 + iTime * 4.0) * 0.5 + 0.5;

    // Combine sine waves with turbulence
    vec2 st = uv * 8.0; // Increase frequency
    float turb = turbulent(st);

    // Create color transitions using HSL
    vec3 color1 = hsl(sin1 * 360.0, 0.7, 0.5);
    vec3 color2 = hsl((sin2 + 0.5) * 360.0, 0.7, 0.5);
    vec3 color3 = hsl((sin3 - 0.5) * 360.0, 0.7, 0.5);

    // Combine colors with turbulence
    vec3 finalColor = (color1 + color2 + color3) * turb;

    return vec4(finalColor, 1.0);
}
