vec4 Effect2(vec2 uv) {
    // Create grid coordinates
    vec2 st = floor(uv * 10.0);

    // Add noise to create shifting pattern
    vec2 offset = vec2(noise(st + iTime), noise(st + iTime + 10.0));

    // Calculate color based on position and time
    float hue = fract((st.x + st.y) * 0.1 + iTime * 0.5);
    vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Add turbulence for dynamic effect
    float turb = turbulent(uv * 10.0 + offset * 5.0);
    color *= 1.0 + turb * 0.5;

    // Create square pattern with varying size
    vec2 cell = fract(uv * 10.0) - 0.5;
    float dist = length(cell);
    float square = smoothstep(0.4, 0.6, dist);

    // Combine effects
    color *= mix(vec3(0.8), vec3(1.0), square);
    return vec4(color, 1.0);
}
