vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create expanding plasma effect using time and radius
    float timeScale = iTime * 0.5;
    float baseColor = sin(r * 8.0 + timeScale) * 0.5 + 0.5;
    // Independent color channel shifts
    vec3 colors;
    colors.r = sin(theta + timeScale * 2.0);
    colors.g = sin(theta + timeScale * 1.5 + PI/2.0);
    colors.b = sin(theta + timeScale + PI);
    // Add turbulence for dynamic noise effect
    float noiseScale = turbulent(vec3(uv * 4.0, iTime)) * 0.5;

    // Combine color channels with noise and base pattern
    vec3 finalColor = hsl2rgb(vec3(
        (baseColor + noiseScale) * 0.6,
        1.0,
        0.8
    )) * colors;
    return vec4(finalColor, 1.0);
}
