vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for hexagonal pattern
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan(st.y, st.x);
    float radius = length(st);

    // Add time-based rotation
    angle += iTime * 0.5;

    // Create hexagonal grid pattern using polar coordinates
    vec2 polar = vec2(angle / (pi * 2.0), radius);
    vec2 grid = floor(polar * 10.0); // Scale and quantize

    // Add turbulence for dynamic movement
    float tx = turbulent(vec3(grid.x + iTime, grid.y, 0.0)) * 0.5;
    float ty = turbulent(vec3(grid.x, grid.y + iTime * 0.7, 1.0)) * 0.5;

    // Calculate final color with RGB shifts
    vec3 color = hsl2rgb(vec3(
        (grid.x + tx) * 0.15,
        (grid.y + ty) * 0.3 + 0.5,
        0.7 + sin(iTime * 0.5 + grid.x * 0.2) * 0.2
    ));

    return vec4(color, 1.0);
}
