vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for hexagonal pattern
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Add time-based rotation
    angle += iTime * 0.5 + sin(iTime) * 0.2;

    // Create hexagonal grid pattern
    vec3 color = hsl(
        fract(angle / (pi/3.0)) * 0.6 + 0.1,
        0.7,
        0.5 + 0.5 * sin(iTime + angle)
    );

    // Add pulsating effect to radius
    float pulse = sin(iTime * 2.0) * 0.3;
    radius += pulse;

    // Create hexagonal mask
    vec2 hexCoords = vec2(
        cos(angle) * radius,
        sin(angle) * radius
    );

    // Add noise texture
    float noiseValue = turbulent(hexCoords * 5.0 + iTime);
    color *= hsl2rgb(vec3(
        fract(iTime / 2.0),
        1.0,
        0.5 + noiseValue * 0.5
    ));

    // Combine patterns and apply mask
    float pattern = length(hexCoords) < 1.0 ? 1.0 : 0.0;
    pattern *= smoothstep(0.8, 1.2, radius);

    return vec4(color * pattern, 1.0);
}
