vec4 Effect2(vec2 uv) {
    // Create a position vector centered at 0.5
    vec2 pos = uv - 0.5;

    // Calculate radius and angle in polar coordinates
    float r = length(pos);
    float theta = atan2(pos.y, pos.x);

    // Add some turbulence to create interesting patterns
    float tr = turbulent(vec3(r * 10.0 + iTime, 0.0, 0.0)) * 0.5;
    float ttheta = turbulent(vec3(theta * 10.0 + iTime, 0.0, 0.0)) * 0.5;

    // Create a color vector using HSL
    vec3 color = hsl(
        (iTime * 0.2 + theta * 0.5) * 180.0 / PI,
        0.7 + tr * 0.3,
        0.5 + ttheta * 0.5
    );

    // Convert HSL to RGB and return as vec4 with alpha
    return vec4(hsl2rgb(color), 1.0);
}
