vec4 Effect2(vec2 uv) {
    // Create a kaleidoscope effect by mirroring coordinates
    vec2 p = mod(uv * 10.0, 1.0);
    p = abs(p - 0.5) * 2.0;

    // Add time-based animation
    float t = iTime * 0.1;

    // Create sine wave pattern with shifting frequencies
    float wave = sin(p.x * 10.0 + t) * cos(p.y * 10.0 - t);

    // Add turbulence for more dynamic patterns
    vec2 noiseUV = uv * 5.0 + t;
    float turb = turbulent(noiseUV);

    // Combine wave and turbulence effects
    float pattern = (wave + turb) * 0.5 + 0.5;

    // Create shifting hues using HSL color space
    vec3 color = hsl(pattern * 360.0, 1.0, 0.5);

    return vec4(color, 1.0);
}
