vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create square pattern using polar coordinates
    float angle = theta + sin(r * 5.0 + iTime) * 0.5;
    vec2 rotatedUV = vec2(
        cos(angle) * st.x - sin(angle) * st.y,
        sin(angle) * st.x + cos(angle) * st.y
    );

    // Add turbulence effect
    float turb = turbulent(rotatedUV * 10.0 + iTime);
    r += turb * 0.2;

    // Create color gradient with time-based hue shift
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.5, 1.0), // Hue that changes over time
        0.7 + turb * 0.3,     // Saturation
        0.5 + cos(iTime) * 0.5 // Lightness with pulsing effect
    ));

    // Combine effects for final output
    return vec4(color * (1.0 - r * 0.5), 1.0);
}
