vec4 Effect2(vec2 uv) {
    // Create flowing patterns using sine waves with varying frequencies
    float time = iTime * 0.5;

    // Complex wave pattern combining multiple frequencies
    vec3 waves = vec3(
        sin(uv.x * 8.0 + time) * cos(uv.y * 4.0 - time),
        sin(uv.y * 6.0 + time * 0.7) * cos(uv.x * 5.0 - time * 0.3),
        sin((uv.x + uv.y) * 12.0 + time) 
    );

    // Add noise and turbulence for organic feel
    vec2 noiseUV = uv * 4.0 + vec2(time, time);
    float t = turbulent(noiseUV);

    // Mix in RGB shifts with time variation
    vec3 colorShifts = vec3(
        sin(time * 0.7) * 0.5 + 0.5,
        cos(time * 0.6) * 0.5 + 0.5,
        sin(time * 0.8 + PI) * 0.5 + 0.5
    );

    // Combine waves and noise with color shifts
    vec3 finalColor = hsl2rgb(vec3(
        (waves.x + t) * 0.5 + 0.5,
        (waves.y + t) * 0.7 + 0.3,
        (waves.z + t) * 0.4 + 0.6
    )) * colorShifts;

    return vec4(finalColor, 1.0);
}
