vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for better pattern size
    vec2 st = uv * 10.0;

    // Create two offset sine waves in X and Y directions
    float waveX = sin(st.x + iTime) * 0.5 + 0.5;
    float waveY = sin(st.y + iTime + PI/2.0) * 0.5 + 0.5;

    // Combine waves with some noise for complexity
    float combined = (waveX + waveY) * 0.5;
    combined += noise(vec2(st.x*0.5, st.y*0.5)) * 0.1;

    // Create color transitions using HSL
    vec3 color = hsl(
        combined * 360.0 + iTime * 100.0,
        0.7,
        0.8
    );

    // Add some turbulence for dynamic background
    float turb = turbulent(vec2(st.x*0.5, st.y*0.5)) * 0.3;
    color += turb;

    return vec4(hsl2rgb(color), 1.0);
}
