vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating pattern with time-based rotation
    theta += iTime * 3.0;

    // Add turbulence effect
    float turb = turbulent(vec3(st * 5.0 + iTime, 0.0)) * 0.5 + 0.5;

    // Create color shifts using HSL
    vec3 hslColor = vec3(
        mod(theta * 8.0 + iTime, 2.0 * PI) / (2.0 * PI),  // Hue variation
        1.0 - turb * 0.5,                                 // Saturation
        0.7 + turb * 0.3                                  // Lightness
    );

    // Convert to RGB and mix with radial gradient
    vec3 rgbColor = hsl2rgb(hslColor);
    float gradient = smoothstep(1.0, 0.0, r);

    // Combine effects
    vec3 finalColor = mix(vec3(0.0), rgbColor, gradient * (1.0 + sin(iTime) * 0.5));

    return vec4(finalColor, 1.0);
}
