vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create a basic sine wave pattern
    vec2 st = uv * 10.0;
    float sinWave = sin(st.x + time) * 0.5 + 0.5;

    // Add geometric pattern with polar coordinates
    vec2 polar = uv * 2.0 - 1.0;
    float radius = length(polar);
    float angle = atan2(polar.y, polar.x) + time;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        sin(time * 0.5) * 0.5 + 0.5,
        0.7,
        0.8
    );

    // Add noise and turbulence for dynamic effect
    float noiseVal = noise(vec2(uv.x * 10.0, time));
    float turbulentVal = turbulent(vec3(uv, time));

    // Combine patterns and colors
    vec3 color = hsl(hslColor.x, hslColor.y, hslColor.z);
    color *= sinWave;
    color += noiseVal * 0.2;
    color += turbulentVal * 0.1;

    return vec4(color, 1.0);
}
