vec4 Effect2(vec2 uv) {
    // Create a time-varying offset for movement
    vec2 st = uv + sin(iTime * 0.5) * 0.1;

    // Add some turbulence at different scales
    float t1 = turbulent(st * 3.0 + iTime);
    float t2 = turbulent(st * 6.0 - iTime);

    // Combine the turbulence values with a mix of time and position
    float noiseFactor = (t1 * 0.5 + t2 * 0.5) * 0.5 + 0.5;

    // Create an eerie color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.2) * 0.6 + 0.4,   // Hue variation over time
        noiseFactor * 0.7 + 0.3,       // Saturation based on noise
        noiseFactor * 0.5 + 0.25       // Lightness based on noise
    ));

    // Add some radial movement and depth
    vec2 toCenter = st - vec2(0.5);
    float distFromCenter = length(toCenter) * 4.0;
    color *= sin(distFromCenter - iTime) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
