vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Add time-based rotation and pulsation
    theta += iTime * 0.5;
    r *= 0.7 + sin(iTime) * 0.3;

    // Create a noise pattern with turbulence
    vec2 noiseScale = vec2(4.0, 4.0);
    float n1 = noise(st * noiseScale + iTime);
    float n2 = turbulent(vec3(st * 2.0, iTime));

    // Combine noise layers and create color gradients
    float gradient = (1.0 - cos(r)) * 0.5;
    vec3 color = hsl(theta * 0.5 + 0.5, 0.7 + n1 * 0.2, 0.5 + sin(iTime) * 0.3);

    // Add pulsating effect
    float pulse = sin(iTime * 2.0) * 0.3;
    color *= 1.0 + pulse;

    return vec4(color, 1.0);
}
