vec4 Effect2(vec2 uv) {
    // Center the coordinates and convert to polar
    vec2 st = uv - 0.5;
    float theta = atan2(st.y, st.x);
    float r = length(st) * 3.0; // Scale radius

    // Create rotating pattern with time
    theta += iTime * 0.5 + sin(iTime) * 0.2;

    // Add concentric circles with noise modulation
    float circleMod = mod(r, 1.0);
    float noiseMod = turbulent(vec3(st.x*2.0, st.y*2.0, iTime)) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        theta * 0.15 + iTime * 0.1,
        0.7 + noiseMod * 0.3,
        0.8 - mod(r, 1.0) * 0.4
    ));

    // Add fading trails effect using time-based noise
    float trail = sin(theta * 5.0 + iTime) * exp(-r * 0.2);
    color += vec3(trail * 0.6);

    // Mix in some radial variation
    color *= 1.0 - smoothstep(0.8, 1.0, r / 3.0);

    return vec4(color, 1.0);
}
