vec4 Effect2(vec2 uv) {
    // Create time-varying offset for pixel movement
    vec2 offset = vec2(
        sin(iTime + uv.y * 10.0) * 0.05,
        cos(iTime + uv.x * 10.0) * 0.05
    );

    // Calculate new UV coordinates with offset
    vec2 newUV = uv + offset;

    // Create color transitions using HSL to RGB conversion
    vec3 baseColor = hsl(180.0, 1.0, 0.5);
    float colorVariation = noise(newUV * 10.0 + iTime) * 0.5 + 0.5;

    // Mix colors with turbulence for dynamic effect
    vec3 finalColor = mix(
        baseColor,
        hsl(240.0, 1.0, 0.5),
        turbulent(newUV * 10.0 + iTime) * 0.5 + 0.5
    );

    // Output the color with some transparency
    return vec4(finalColor, 1.0);
}
