vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for better noise pattern visibility
    vec2 st = uv * 50.0;

    // Create flowing movement using sine wave patterns
    float dirX = sin(iTime) * 0.5 + 0.5;
    float dirY = cos(iTime) * 0.5 + 0.5;

    // Add turbulence to create organic flow patterns
    float turb = turbulent(st * 0.1);

    // Combine movement and turbulence for final color offset
    vec3 colorOffset = mix(vec3(dirX, dirY, 0.0), vec3(turb), 0.5);

    // Create pulsating colors using HSL conversion
    float hue = iTime * 0.2 + sin(iTime * 2.0) * 0.5;
    vec3 hslColor = vec3(hue, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine everything for final output
    return vec4(rgbColor * colorOffset, 1.0);
}
