vec4 Effect2(vec2 uv) {
    float scale = 10.0;
    vec2 grid = floor(uv * scale) / scale;

    // Create rotating tessellation pattern
    float angle = iTime * 0.5;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedUV = (uv - 0.5) * rot + 0.5;

    // Generate color variations using noise and turbulence
    float noise1 = noise(vec3(grid, iTime * 0.1));
    float noise2 = turbulent(rotatedUV * 4.0);

    // Create dynamic color transitions
    vec3 color1 = hsl(0.5 + noise1 * 0.5, 0.8, 0.6);
    vec3 color2 = hsl(0.3 + iTime * 0.1, 0.7, 0.5);

    // Mix colors based on position and time
    vec3 finalColor = mix(color1, color2, smoothstep(0.0, 1.0, noise2));

    // Add gradient effect
    float gradient = length(uv - 0.5) * 0.5 + 0.5;
    finalColor = mix(finalColor, vec3(gradient), 0.3);

    return vec4(hsl2rgb(finalColor), 1.0);
}
