vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float r = length(uv);

    // Create pulsing effect using time
    float pulse = sin(iTime * 0.5 + r * 10.0) * 0.5;

    // Create circular pattern with pulsing radius
    float circ = mod(r + pulse, 1.0);

    // Add turbulence for dynamic effect
    vec2 st = uv * 8.0;
    float turb = turbulent(st);

    // Mix colors using HSL to RGB conversion
    vec3 color = hsl(circ * 0.5 + iTime*0.5, 1.0, circ * 0.5 + 0.5);

    // Add turbulence effect to color
    color = mix(color, vec3(0), turb * 0.2);

    return vec4(color, 1.0);
}
