vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 8.0 + iTime;

    // Create interweaving sine wave patterns
    float wave1 = sin(st.x * 0.5 + iTime) * 0.5 + 0.5;
    float wave2 = sin(st.y * 0.5 + iTime + PI/2.) * 0.5 + 0.5;
    float wave3 = sin((st.x + st.y) * 0.3 + iTime + PI) * 0.5 + 0.5;

    // Mix RGB colors with time-based shifting
    vec3 color1 = vec3(1.0, 0.2, 0.2); // Red
    vec3 color2 = vec3(0.2, 1.0, 0.2); // Green
    vec3 color3 = vec3(0.2, 0.2, 1.0); // Blue

    float mixFactor = sin(iTime * 0.5) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixFactor);
    finalColor = mix(finalColor, color3, mixFactor * 0.5);

    // Combine waves and add noise for complexity
    float value = (wave1 + wave2 + wave3) / 3.0;
    value += turbulent(st * 4.0 + iTime) * 0.3;

    // Create final output color
    vec3 result = finalColor * value;

    return vec4(result, 1.0);
}
