//prompt: something awesome
vec4 Effect2(vec2 uv) {
    // Create rotating polar pattern
    vec2 polar = uv * 2.0 - 1.0;
    float radius = length(polar);
    float angle = atan(polar.y, polar.x) + iTime * 0.5;

    // Generate three concentric rings with different frequencies
    float a = sin(radius * 8.0 + angle * 2.0) * 0.5 + 0.5;
    float b = sin(radius * 6.0 + angle * 3.0) * 0.5 + 0.5;
    float c = sin(radius * 4.0 + angle * 4.0) * 0.5 + 0.5;

    // Create color from three layers
    vec3 color = vec3(
    a * 0.6 + b * 0.3,
    b * 0.4 + c * 0.5,
    a * 0.2 + c * 0.8
    );

    // Add glow effect at edges
    float glow = smoothstep(1.0, 0.9, radius);
    color = mix(color, vec3(1.0), glow * 0.4);

    return vec4(clamp(color, 0.0, 1.0), 1.0);
}
