vec4 Effect2(vec2 uv) {
    // Create a base noise pattern with time variation
    vec2 st = uv * 5.0 + iTime;

    // Generate multi-scale noise patterns
    float n1 = noise(st);
    float n2 = noise(st * 0.5 + 3.0*iTime);
    float n3 = noise(st * 0.25 - 2.0*iTime);

    // Create RGB color shifts using HSL conversion
    vec3 colors;
    colors.r = hsl(n1 * 0.4 + 0.6, 0.8, 0.5).r;
    colors.g = hsl(n2 * 0.4 + 0.2, 0.7, 0.6).g;
    colors.b = hsl(n3 * 0.4 + 0.8, 0.6, 0.7).b;

    // Mix colors with turbulence
    float turb = turbulent(vec3(uv * 10.0, iTime));
    vec3 finalColor = mix(colors.rbg, colors.gbr, turb);

    return vec4(finalColor, 1.0);
}
