vec4 Effect2(vec2 uv) {
    // Create a base color using sine waves with different frequencies
    vec3 color = sin(vec3(
        uv.x * 5.0 + iTime,
        uv.y * 4.0 + iTime * 0.5,
        1.0
    )) * 0.5 + 0.5;

    // Add multiple sine layers for more complex transitions
    color += sin(vec3(
        uv.x * 2.0 + iTime * 2.0,
        uv.y * 3.0 + iTime * 1.5,
        1.0
    )) * 0.3;

    // Add turbulent noise for extra detail and randomness
    vec2 noiseUV = uv * 4.0 + vec2(iTime);
    float turbulence = turbulent(noiseUV) * 0.3;
    color += turbulence;

    // Create a pulsing effect using time-based modulation
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;
    color *= pulse;

    // Convert to HSL for better color control and mix with another sine layer
    vec3 hslColor = hsl(
        sin(uv.x * 2.0 + iTime) * 0.4 + 0.5,
        sin(uv.y * 2.0 + iTime * 0.7) * 0.6 + 0.3,
        0.8
    );

    // Combine both color approaches with some blending
    color = mix(color, hslColor, 0.4);

    // Add final noise layer for subtle texture
    vec2 finalNoiseUV = uv * 10.0 + vec2(iTime * 0.3);
    float finalTurbulence = turbulent(finalNoiseUV) * 0.1;
    color += finalTurbulence;

    // Ensure colors stay within valid range and return as vec4
    return vec4(clamp(color, 0.0, 1.0), 1.0);
}
