vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for hexagonal symmetry
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * pi/6.0; // Add rotation

    // Create hexagonal pattern using polar coordinates
    float angle = theta / (pi/3.0); // Hexagon has 6-fold symmetry
    float d = abs(length(vec2(r, r - 1.0)));

    // Add turbulence for dynamic effect
    vec3 noisePos = vec3(st.x + iTime * 0.5, st.y + iTime * 0.5, iTime);
    float turb = turbulent(noisePos) * 0.2;

    // Create pulsating color effect
    float hue = fract(iTime * 0.1 + sin(r * 3.0 + theta));
    vec3 color = hsl(hue, 1.0, 0.5);

    // Mix colors based on distance from center and turbulence
    vec3 finalColor = mix(color, vec3(1.0), smoothstep(0.8, 1.2, r + turb));

    return vec4(finalColor, 1.0);
}
