vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating pattern by offsetting UV coordinates
    vec2 st = uv - 0.5;
    float angle = time;
    st = vec2(
        st.x * cos(angle) - st.y * sin(angle),
        st.x * sin(angle) + st.y * cos(angle)
    ) + 0.5;

    // Create spiral pattern using polar coordinates
    vec2 toCenter = st - 0.5;
    float r = length(toCenter);
    float theta = atan(toCenter.y, toCenter.x);

    // Add spiral arms
    float spiral = sin(theta * 5.0 + time) * 0.1;
    r += spiral;

    // Create color variations using HSL
    vec3 color = hsl(
        0.6 + (r * 0.2),   // Hue variation based on radius
        0.7 + sin(r * 5.0 + time) * 0.1,  // Saturation variation
        0.3 + turbulent(st * 4.0) * 0.2    // Brightness with noise
    );

    // Add turbulence for more dynamic effect
    float brightness = turbulent(st * 8.0);
    color *= brightness;

    // Create starry background
    vec3 finalColor = mix(vec3(0.0), color, smoothstep(0.0, 1.0, r));

    return vec4(finalColor, 1.0);
}
