vec4 Effect2(vec2 uv) {
    vec3 finalColor = vec3(0.0);

    // Create a grid of points with noise offset
    vec2 st = uv * 10.0;
    vec2 pos = floor(st + 0.5) + noise(vec2(floor(st))) * 0.5;

    // Animate position over time with sine wave pattern
    float time = iTime * 0.5;
    pos.y += sin(time + pos.x) * 0.3;

    // Calculate distance from current fragment to particle center
    vec2 toParticle = pos - st;
    float dist = length(toParticle);

    // Create glowing effect with turbulence
    float glow = exp(-dist * 5.0) * (1.0 + turbulent(vec3(pos, time)) * 0.5);

    // Add color variation using HSL
    vec3 hslColor = vec3(0.6 + sin(time + pos.x) * 0.2, 0.8, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    finalColor += glow * rgbColor;

    // Add some background color
    finalColor += vec3(0.05);

    return vec4(finalColor, 1.0);
}
