vec4 Effect2(vec2 uv) {
    // Add time-based movement and scale coordinates
    vec2 st = uv * 5.0 + iTime;

    // Create flowing sine wave pattern with dynamic frequency
    float wave1 = sin(st.x * 0.5 + iTime * 2.0);
    float wave2 = sin(st.y * 0.7 - iTime * 3.0);

    // Add noise for organic texture
    vec2 noisePos = st * 0.5;
    float noiseVal = noise(noisePos) * 0.5 + 0.5;

    // Combine waves and noise with turbulence
    float pattern = (wave1 + wave2) * 0.5 + noiseVal * turbulent(st);

    // Create dynamic color shifts using HSL
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.1 + st.x * 0.2),   // Hue variation over time and space
        0.7,                                // Saturation
        0.5 + pattern * 0.4                // Brightness modulation
    ));

    return vec4(color, 1.0);
}
