vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates around center with time-based rotation
    vec2 st = uv * 2.0 - 1.0;
    float angle = iTime * 0.5;
    mat2 rot = mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
    vec2 rotatedUV = rot * st;
    // Add noise and turbulence
    float noiseValue = turbulent(rotatedUV * 4.0 + iTime) * 0.5 + 0.5;
    float pattern = sin(length(rotatedUV) * 8.0 + iTime) * 0.5 + 0.5;
    // Create geometric patterns with rotating colors
    vec3 color = hsl(
        mod(iTime * 0.2 + noiseValue * 2.0, 1.0),  // Hue variation over time and noise
        0.7 + pattern * 0.3,                       // Saturation modulation
        0.5 + sin(length(rotatedUV) * 4.0 + iTime) * 0.5  // Brightness variation
    );
    // Convert HSL to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
