//prompt: a fractal
vec4 Effect2(vec2 uv) {
    // Mandelbrot fractal
    const float maxIterations = 20.0;
    vec2 c = (uv - 0.5) * 3.0; // Scale and center the coordinates
    vec2 z = vec2(0.0);

    float iterations = 0.0;
    while (iterations < maxIterations && length(z) <= 2.0) {
    vec2 nextZ = vec2(
    z.x*z.x - z.y*z.y + c.x,
    2.0*z.x*z.y + c.y
    );
    z = nextZ;
    iterations++;
    }

    // Color based on iteration count
    float colorVal = length(z) > 2.0 ? iterations / maxIterations : 0.0;

    vec3 color = vec3(
    sin(colorVal * 3.0 + 1.0),
    sin(colorVal * 5.0 + 2.0),
    sin(colorVal * 7.0 + 3.0)
    );

    return vec4(color, 1.0);
}
