vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add rotation based on time
    theta += 0.5 * iTime;
    // Create spiral pattern using turbulence
    float spiral = turbulent(vec3(r * 5.0 + theta, r * 3.0 - theta, 0.0)) * 0.5 + 0.5;

    // Color based on angle and radius
    vec3 color = hsl2rgb(vec3(
        (theta * 0.15 + iTime * 0.1) * 360.0,
        0.7 + spiral * 0.3,
        0.5 + sin(r * 4.0 - iTime) * 0.2
    ));
    // Create circular mask for galaxy shape
    float mask = smoothstep(1.0, 0.98, r);
    return vec4(color * mask, 1.0);
}
