vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating pattern using sine functions
    vec2 st = uv * 10.0;
    st.x += sin(time + st.y * 2.0) * 0.5;
    st.y += cos(time + st.x * 2.0) * 0.5;

    // Add turbulence for plasma effect
    float turb = turbulent(st);

    // Dynamic color shifts using HSL
    vec3 color = hsl(
        0.5 + sin(time * 0.5) / 4.0,   // Hue variation over time
        0.8,                            // Saturation
        0.5 + turb * 0.5               // Lightness modulation
    );

    // Add another layer of turbulence for more detail
    color += hsl2rgb(vec3(
        0.7,
        0.6,
        0.4 + turbulent(st * 5.0) * 0.6
    )) * 0.5;

    return vec4(color, 1.0);
}
