vec4 Effect2(vec2 uv) {
    // Create a base noise pattern with time offset
    float noiseValue = noise(uv * 5.0 + vec2(iTime, iTime));

    // Generate dynamic color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.1, 1.0),  // Hue that changes over time
        0.7,                    // Saturation
        0.5 + noiseValue * 0.4  // Lightness with noise variation
    ));

    // Add turbulence for more dynamic movement
    float turb = turbulent(uv * 3.0 + vec2(iTime));

    // Combine noise and turbulence effects
    color *= mix(1.0, 0.5, abs(turb) * 0.5 + 0.5);

    return vec4(color, 1.0);
}
