vec4 Effect2(vec2 uv) {
    // Create base noise pattern with time variation
    float noise1 = turbulent(uv * 5.0 + iTime);
    float noise2 = turbulent(uv * 3.0 + iTime * 0.5);

    // Generate color components using HSL colors
    vec3 color1 = hsl(noise1 * 0.6 + 0.4, 0.8, 0.5);
    vec3 color2 = hsl((noise2 * 0.6 + 0.4) + iTime * 0.1, 0.7, 0.6);

    // Combine colors with time-based blending
    vec3 finalColor = mix(color1, color2, sin(iTime * 0.5) * 0.5 + 0.5);

    // Add pulsating glow effect using alpha channel
    float glow = turbulent(uv * 2.0 + iTime) + length(uv) * 0.5;
    finalColor *= clamp(glow, 0.0, 1.0);

    return vec4(finalColor, clamp(glow * 0.5 + 0.5, 0.0, 1.0));
}
