vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create orbiting effect using time and rotation
    a += sin(iTime * 0.5 + a) * 0.5;
    r += cos(iTime + a * 3.0) * 0.1;
    // Add noise for texture
    vec2 noisePos = vec2(r, a);
    float n = noise(noisePos * 0.5 + iTime);
    // Create color transitions using HSL
    vec3 color;
    color.x = fract(iTime * 0.2 + r * 0.5) * 0.7 + 0.3; // Hue
    color.y = 1.0; // Saturation
    color.z = abs(n * 0.5 + 0.5); // Lightness
    // Convert HSL to RGB and add noise effect
    vec3 rgbColor = hsl2rgb(color);
    rgbColor *= (1.0 - n * 0.5);
    // Create geometric pattern based on radius
    float shape = sin(r * 8.0 + iTime) * 0.5 + 0.5;
    rgbColor *= mix(0.3, 1.0, shape);
    return vec4(rgbColor, 1.0);
}
