vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 st = uv - 0.5;

    // Calculate radius and angle
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Create moving pattern using time
    float time = iTime * 0.5;

    // Add noise and turbulence
    float noiseVal = turbulent(vec3(r + time, theta, 0.0));

    // Color based on angle with RGB shifts
    vec3 color = hsl2rgb(vec3(theta * 180.0 / PI + noiseVal * 50.0, 0.7, 0.6));

    // Create concentric circles effect
    float circle = sin(r * 10.0 + time) * 0.5 + 0.5;

    // Combine effects
    color *= circle;

    return vec4(color, 1.0);
}
