vec4 Effect2(vec2 uv) {
    // Center coordinates and scale for better pattern visibility
    vec2 st = (uv - 0.5) * 3.0;

    // Time-based rotation angle
    float time = iTime * 0.5;
    mat2 rot = mat2(cos(time), sin(time), 
                    -sin(time), cos(time));

    // Apply rotation and scale
    vec2 transformed = st * rot * 1.5;

    // Create wave patterns with sine functions
    float patternX = abs(sin(transformed.x * 0.7 + time)) - 0.5;
    float patternY = abs(sin(transformed.y * 0.7 + time)) - 0.5;

    // Combine patterns and create geometric shapes
    vec2 shape = vec2(patternX, patternY);
    float distance = length(shape);

    // Add movement with another frequency
    transformed += sin(time * 0.3) * 0.5;
    float pattern2 = abs(sin(length(transformed) * 4.0 + time)) - 0.5;

    // Combine both patterns for complex shapes
    float finalPattern = distance + pattern2 * 0.7;

    // Create color shifts using HSL
    vec3 color = hsl(finalPattern * 0.6 + 0.5, 
                     1.0 - abs(finalPattern), 
                     0.5);

    return vec4(hsl2rgb(color), 1.0);
}
