vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form centered at middle of screen
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float a = atan(pos.y, pos.x) + iTime; // Add time for rotation

    // Create swirling effect by modifying angle and radius
    a += sin(r * 5.0 + iTime) * 0.5;

    // Create pulsating rings using radius and time
    float ring = sin(r * 10.0 + iTime * 2.0);
    ring = (ring + 1.0) * 0.5; // Scale to 0-1 range

    // Create color transitions using HSL colors
    vec3 baseColor = hsl(a * 0.5, 0.7, 0.6);
    vec3 secondaryColor = hsl((a + PI) * 0.5, 0.7, 0.4);

    // Combine colors with pulsating rings
    vec3 color = mix(baseColor, secondaryColor, ring);

    // Add noise for extra texture
    float noiseValue = noise(vec2(a * 0.1, r * 0.1)) * 0.2;
    color += noiseValue;

    return vec4(color, 1.0);
}
