vec4 Effect2(vec2 uv) {
    // Create flowing sine wave patterns with time offset
    float time = iTime * 0.5;

    // Create x and y based sine waves with different frequencies
    float waveX = sin(uv.x * 8.0 + time) * 0.5 + 0.5;
    float waveY = sin(uv.y * 4.0 - time) * 0.5 + 0.5;

    // Mix waves and add noise for turbulence
    vec2 mixUV = uv + vec2(waveX, waveY) * 0.3;
    float noiseVal = noise(mixUV * 10.0);

    // Create color transitions using HSL
    vec3 colorA = hsl(0.5 + noiseVal * 0.4, 0.8, 0.6);
    vec3 colorB = hsl(0.2 + noiseVal * 0.4, 0.7, 0.8);

    // Combine colors with turbulence
    float blend = turbulent(vec2(uv.x * 2.0 + time, uv.y)) * 0.5 + 0.5;
    vec3 finalColor = mix(colorA, colorB, blend);

    return vec4(finalColor, 1.0);
}
