vec4 Effect2(vec2 uv) {
    // Create a time-varying frequency
    float t = iTime * 0.5;

    // Generate horizontal and vertical patterns with phase shifts
    vec3 lines = vec3(
        sin(uv.x * 8.0 + t) + 0.5,
        cos(uv.y * 8.0 - t) + 0.5,
        sin(uv.x * 4.0 + uv.y * 4.0 + t*2.0)
    );

    // Add diagonal patterns with varying frequencies
    lines += vec3(
        sin(uv.x * 16.0 - uv.y * 8.0 + t) * 0.5,
        cos(uv.x * 8.0 + uv.y * 16.0 - t) * 0.5,
        sin(uv.x * 24.0 + uv.y * 24.0 + t*3.0)
    );

    // Create smooth transitions between lines
    float brightness = max(
        smoothstep(0.9, 1.0, abs(lines.x)),
        smoothstep(0.9, 1.0, abs(lines.y))
    ) * max(
        smoothstep(0.95, 1.0, abs(lines.z)),
        0.2
    );

    // Generate color based on time and position
    vec3 color = hsl2rgb(vec3(
        mod(t * 0.1 + uv.x * 4.0 + uv.y * 6.0, 1.0),
        0.7,
        0.5 + brightness * 0.5
    ));

    // Add noise for subtle variations
    color *= 1.0 + noise(vec3(uv, t)) * 0.2;

    return vec4(color, 1.0);
}
