vec4 Effect2(vec2 uv) {
    // Create a grid pattern with sine wave distortion
    vec2 st = uv * 10.0;

    // Add time-based distortion to the coordinates
    float xDistort = sin(st.x + iTime) * 0.5;
    float yDistort = cos(st.y - iTime) * 0.5;

    // Create a moving grid pattern using sine waves
    vec2 grid = vec2(
        sin(st.x + iTime * 0.5) + xDistort,
        cos(st.y - iTime * 0.3) + yDistort
    );

    // Use turbulence to add more organic movement
    float turb = turbulent(vec3(grid, iTime)) * 0.2;

    // Combine the distortions and create a color pattern
    vec3 color = hsl(
        mod(st.x + st.y + iTime, PI) / PI,
        1.0,
        0.5 + turb
    );

    // Create light rays effect using distance from center
    float dist = length(uv - vec2(0.5));
    color *= smoothstep(0.7, 0.0, dist);

    return vec4(color, 1.0);
}
