vec4 Effect2(vec2 uv) {
    // Center and scale UV coordinates
    vec2 st = (uv - 0.5) * 16.0;

    // Add rotation based on time
    float angle = iTime * 0.5;
    float sa = sin(angle);
    float ca = cos(angle);
    mat2 rot = mat2(ca, -sa, sa, ca);
    st = rot * st;

    // Create rotating sine wave patterns
    float x = sin(st.x * 0.1 + iTime) * 4.0;
    float y = sin(st.y * 0.15 + iTime * 0.8) * 3.0;
    float d = abs(x + y);

    // Create color shifts using HSL
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.1) * 360.0,   // Hue variation over time
        0.7,                          // Saturation
        0.5 + d * 0.4                 // Lightness modulation
    ));

    return vec4(color, 1.0);
}
